create  procedure getsegments(in p_segname varchar(45), in occurrences int, in seed int, in p_datasource varchar(45))
begin
	select 
		f1, f2, f3, f4, f5, f6, f7, f8, f9, f10,  
		f11, f12, f13, f14, f15, f16, f17, f18, f19, f20,  
