drop procedure getfields;

delimiter // 
CREATE PROCEDURE getfields(in p_fieldname varchar(45), in p_segname varchar(45), in occurrences int, in seed int, in p_datasource varchar(45))
BEGIN
    SET @s = CONCAT('SELECT ', p_fieldname,' FROM segments where segname = \'', 
				p_segname ,'\' and datasource like \'', p_datasource,
                '\' order by rand(', seed, ') limit ', occurrences);
    PREPARE stmt FROM @s;
    EXECUTE stmt;
    DEALLOCATE PREPARE stmt;
END
